// Parse Input - Configure search parameters
const input = $input.first().json;

// Expected input from parent workflow:
// - targetEmail: the email address to search for (recipient)
// - subjectFilter: subject line to search for
// - senderFilter: optional sender filter
// - maxRetries: max retry attempts (default 10)
// - waitSeconds: seconds between retries (default 15)

return {
  json: {
    targetEmail: input.targetEmail || input.current_business_email,
    subjectFilter: input.subjectFilter || 'Wallester user account activation code',
    senderFilter: input.senderFilter || '',
    maxRetries: input.maxRetries || 10,
    waitSeconds: input.waitSeconds || 15,
    searchQuery: `"${input.subjectFilter || 'Wallester user account activation code'}"`,
    timestamp: new Date().toISOString()
  }
};

// Initialize retry counter and pass through config
const config = $input.first().json;

return {
  json: {
    ...config,
    retryCount: 0
  }
};

// Multi-pattern OTP extraction from email
const items = $input.all();

if (items.length === 0) {
  throw new Error('No emails found');
}

// Get the most recent email
const email = items[0].json;
const subject = email.subject || '';
const snippet = email.snippet || '';
const body = email.plainText || email.textPlain || email.snippet || '';

// Get target email from Parse Input for validation
const targetEmail = $node['Parse Input'].json.targetEmail;

// Check if email matches target recipient
const emailTo = email.to || email.labelIds || '';
const emailToStr = typeof emailTo === 'string' ? emailTo : JSON.stringify(emailTo);

// Combine all text sources
const fullText = `${subject} ${snippet} ${body}`;

// OTP Extraction Patterns (по важност)
const patterns = [
  /\b(\d{6})\b/,                      // 6-digit code
  /\b(\d{4})\b/,                      // 4-digit code
  /code[:\s]*(\d{4,6})/i,            // "code: 123456"
  /OTP[:\s]*(\d{4,6})/i,             // "OTP: 123456"
  /verification[^\d]+(\d{4,6})/i,    // "verification code 123456"
  /confirm[^\d]+(\d{4,6})/i,         // "confirmation code 123456"
  /your code is[^\d]+(\d{4,6})/i,    // "your code is 123456"
  /enter[^\d]+(\d{4,6})/i,           // "enter 123456"
  /\b(\d{5})\b/                       // 5-digit fallback
];

let extractedCode = null;
let patternUsed = null;

// Try each pattern
for (let i = 0; i < patterns.length; i++) {
  const match = fullText.match(patterns[i]);
  if (match && match[1]) {
    extractedCode = match[1];
    patternUsed = patterns[i].toString();
    break;
  }
}

// Also check for verification links
const linkPattern = /https?:\/\/[^\s]+verify[^\s]*/i;
const linkMatch = fullText.match(linkPattern);
const verificationLink = linkMatch ? linkMatch[0] : null;

if (!extractedCode && !verificationLink) {
  throw new Error('Could not extract OTP code or verification link from email');
}

return {
  json: {
    success: true,
    code: extractedCode,
    verification_link: verificationLink,
    email_id: email.id,
    email_subject: subject,
    email_from: email.from,
    pattern_used: patternUsed,
    full_text_preview: fullText.substring(0, 200),
    timestamp: new Date().toISOString()
  }
};

// Success Output - return the extracted code
const data = $input.first().json;

return {
  json: {
    success: true,
    code: data.code,
    verification_link: data.verification_link,
    email_id: data.email_id,
    email_subject: data.email_subject,
    email_from: data.email_from,
    pattern_used: data.pattern_used,
    timestamp: data.timestamp
  }
};

// Timeout Error - max retries exceeded
const retryCount = $input.first().json.retryCount;
const maxRetries = $node['Parse Input'].json.maxRetries;
const waitSeconds = $node['Parse Input'].json.waitSeconds;
const totalWaitTime = retryCount * waitSeconds;

return {
  json: {
    success: false,
    error: 'EMAIL_TIMEOUT',
    message: `Email not received after ${maxRetries} attempts (${totalWaitTime}s)`,
    retriesAttempted: retryCount,
    filters: {
      sender: $node['Parse Input'].json.senderFilter,
      subject: $node['Parse Input'].json.subjectFilter
    },
    timestamp: new Date().toISOString()
  }
};

// Error Output
const data = $input.first().json;

return {
  json: {
    success: false,
    error: data.error,
    message: data.message,
    retriesAttempted: data.retriesAttempted,
    filters: data.filters,
    timestamp: data.timestamp
  }
};
